INSERT INTO node.companyNews (newsId, newsTitle, imgUrl, author, content, pubDate, newsType, newsTypeText) VALUES ('445e77c4-03ad-11e7-b44a-a0999b094dbd', '新闻2', '/images/jj.png', 'lxg', '日前，2016全球冷链物流峰会在宁波顺利召开，与会专家十分看好中国冷链物流市场发展。近日，国家商务部网站对2016年冷链物流发展中央财政支持省市评审结果进行了公示。山东、河南、重庆、宁波、新疆、河北、广东、四川、青海、宁夏，这十个省市将获得数亿元中央财政资金支持。无论是市场机遇还是政策利好，都为我国冷链物流行业送来了橄榄枝，市场发展值得期待。', '2017-03-03', 'companyNews', '公司新闻');
